mapper.select:
SELECT 

FROM certificate_info a

WHERE a.id = #{id}

SELECT
FROM certificate_info a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.certificate_code LIKE '%'||#{certificateCode}||'%' '%'+#{certificateCode}+'%' concat('%',#{certificateCode},'%')
AND a.certificate_name LIKE '%'||#{certificateName}||'%' '%'+#{certificateName}+'%' concat('%',#{certificateName},'%')
AND a.office_id = #{office.id}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM certificate_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE certificate_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
