mapper.select:
SELECT 

FROM certificate_library a

WHERE a.id = #{id}

SELECT 

FROM certificate_library a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.certificate_name LIKE 
			'%'||#{certificateName}||'%'
			'%'+#{certificateName}+'%'
			concat('%',#{certificateName},'%')


		AND a.effective_date_start >= #{effectiveDateStart}


		AND a.effective_date_end <= #{effectiveDateEnd}


		AND a.status = #{status}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM certificate_library a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE certificate_library SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
